USE [Inventoried]

UPDATE [<table_name>]
SET
  [timestamp] = @timestamp,
	[agent] = @agent,
	[table] = @table,
	[operation] = @operation,
	[info] = @info
WHERE [id] = @id;

IF @@ROWCOUNT=0
BEGIN
  INSERT INTO [<table_name>] (
    [id],
		[timestamp],
		[agent],
		[table],
		[operation],
		[info]
  )
  VALUES (
    @id,
		@timestamp,
		@agent,
		@table,
		@operation,
		@info
  )
ENDssignmentId],
		[tagIds],
		[active]
  )
  VALUES (
    @id,
		@serialNumber,
		@categoryId,
		@manufacturerId,
		@notes,
		@assignmentId,
		@tagIds,
		@active
  )
END